<script>
import { mapGetters, mapActions, mapMutations, mapState } from 'vuex';

export default {
  computed: {
    ...mapState('hotels',['selectedHotel']),
  },
  watch: {
  '$i18n.locale': function(newVal, oldVal) {
    this.fetchHotelData()
  }
  },
  methods: {
    ...mapGetters('hotels', ['getSelectedHotel']),
    ...mapMutations('hotels', ['setSelectedHotel']),
    ...mapActions('hotels', ['fetch_hotel_by_id']),
     fetchHotelData() {
      const hotelId = this.$route.params.id;
      this.fetch_hotel_by_id({ hotelId });
    },
  },
  async created() {
    if(this.$store.state.hotels.selectedHotel.length == 0){
      await this.fetchHotelData()
    }
  },


};
</script>

<style>
._id_page_wrapper{
  padding-top: 12vh;
}
.hotel_description{
  font-weight: 500;
  font-size: var(--fs_xs_500);
  letter-spacing: -2.3%;
  line-height: 180%;
  margin-bottom: 40px;
}
.hotel_images{
  display: flex;
  justify-content: flex-start;
  align-items: center;
  width: 100%;
  flex-direction: column;
  gap: 24px;
}
.hotel_images .sec1{
  display: flex;
	flex-wrap: wrap;
	align-items: center;
  width: 100%;
  gap: 24px;
}
.hotel_images .sec2{
  display: flex;
	flex-wrap: wrap;
	align-items: center;
  justify-content: flex-end;
  width: 100%;
  gap: 24px;
}
.hotel_images .sec2 .image{
  width: calc(((100% / 3) * 1) - (48px / 3));
  height: 340px;
}
.hotel_images .sec2 .image img{
  width: 100%;
  height: 100%;
}
.hotel_images .sec1 .item-0 {
	flex-grow: 1.5;
	flex-shrink: 2;
	align-self: flex-start;
  width: calc(((100% / 2) ) - 12px);
  height: 340px;
}
.hotel_images .sec1 .item-1 {
	flex-grow: 1;
	flex-shrink: 2;
  width: calc((100% / 2) - 12px);
  height: 340px;
}
.hotel_images .sec1 img{
  width: 100%;
  height: 100%;
}
.activity_imgs{
  display: flex;
  gap: 24px;
  flex-wrap: wrap;
  width: 100%;
}
.hasServices{
  display: flex;
  justify-content: center;
  align-items: center;
  margin-bottom: 40px;
  gap: 44px;
  flex-wrap: wrap;
}
.hasServices .it_has_container{
  display: flex;
  flex-direction: column;
}
@media(max-width: 700px) {
  .hotel_images .sec1 .item-0, .hotel_images .sec1 .item-1 , .hotel_images .sec2 .image{
    height: 200px;
  }
  .hotel_images .sec2 .image{
    width: calc((100% / 2) - 12px);
  }
  .hotel_images .sec1 .item-0 {
    width: calc(((100% / 2) ) - 12px);
  }
  .hotel_images .sec1 .item-1 {
    width: calc((100% / 2) - 12px);
  }
  .hasServices{
    gap: 24px;
  }
}
@media (max-width:400px){
  .hotel_images .sec1, .hotel_images .sec2{
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    width: 100%;
  }
  .hotel_images .sec1 .item-0 ,.hotel_images .sec1 .item-1 , .hotel_images .sec2 .image{
    width: 100%;
  }
}
</style>
